
public class Aufgabe_Methode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double zahl1 = 3.547;
		double zahl2 = 6.354;
		
		double ergebnis = produkt(zahl1, zahl2);
		
		System.out.println("Ergebnis: " + ergebnis);
	}
	
	public static double produkt (double zahl1, double zahl2) {
		
		double ergebnis = zahl1* zahl2;
		
		
		
		
		return ergebnis;
	}
	
}
